import React from 'react';

import { AboutDescription } from './about.styles';

const AboutPage = (props) =>{

  return (
    <>
      <h1>About</h1>
      <AboutDescription><strong>Description and Mission Statement:</strong><br/>Definition Music is a Mixing, Mastering, Production studio and BLOG. Whether getting a track or album ready for release to a myriad of different formats and streaming playback; or working on a new production, audio is my passion.</AboutDescription>

      <AboutDescription><strong>About:</strong><br/>As a longtime DJ and musician, Marcus Norwood, aka DJ Definition (hiphop) and M.D. Norwood (garage rock & soul), started Definition Music to represent his expanding studio and 20+ years of experience in audio.
      Focused on a wide range of recording, production and engineering techniques, with the hope of turning the studio into a full fledged tracking facility with high quality hybrid solutions and a scenic mountain view in Colorado.</AboutDescription>
      <AboutDescription>Let me know how you'd like me to help on your project. Contact me today to get rates and book my services.</AboutDescription>
      <button onClick={() => props.history.push('/')}>Home</button>
  </>
  )
};




export default React.memo(AboutPage);
