import React from 'react';
import { BannerContainer, BannerText } from './banner.styles';
import AudioMeter from './../../components/audio-meter-animation/audio-meter.component';
import MailchimpForm from '../mailchimp-form/mailchimp-form.component';
const Banner = () => {

  return (


  <BannerContainer>
    <AudioMeter />
      <BannerText>Subscribe to get special rates on mastering, cleanup or repair</BannerText>
        <div id="mc_embed_signup">
          <MailchimpForm />
        </div>
  </BannerContainer>
  )
};

export default React.memo(Banner);
