import React from 'react';

import { } from './file-upload.style';

const FileUploader = () => {

  const handleUpload = (evt) => {
    let file = evt.target.files[0];
    const musicRef = firebase.storage().ref('music/' + file.name)

    musicRef.put(file)
      .then(() => {
        const storageRef = firebase.storage().ref('/music')
        storageRef.child(file).name.getMetadata()
      .then(metaData => {
        let url = metaData.downloadURLs[0]
        const messageRef = firebase.firestore().ref('message');
        messageRef.push({
          song: url,
          songName: file.name
        })
      })
    }).catch(error => console.error(error));
   };

  return (
    <>
    <h2>Upload Files</h2>

    <input type='file' onChange={handleUpload}>Upload</input>


    </>
  );
}

export default FileUploader;
