import React from 'react';
import { Link } from 'react-router-dom';
import { BannerContainer, BannerText, HeaderLineBreak } from './banner.styles';
import CustomButton from '../custom-button/custom-button.component';
import AudioMeter from './../../components/audio-meter-animation/audio-meter.component';
import MailchimpSubscribeForm from '../mailchimp-form/mailchimp-subscribe-form.component';

const Banner = () => {

  return (


  <BannerContainer>
  <AudioMeter />
  <BannerText>
              Subscribe for studio rates<HeaderLineBreak />And Special Deals
              <div id="mc_embed_signup">
                <MailchimpSubscribeForm />
              </div>
            </BannerText>
  <BannerText>
    <span className='' style={{fontSize: '2.3em'}}>FRESH BEATS FOR SALE</span>
    <HeaderLineBreak />
    <span className='' style={{fontSize: '.5em', lineHeight: '1em'}}>You keep 100% royalties as an artist when you publish
    <HeaderLineBreak />
    Stems, Unmastered & Mastered Wav, & NFTs available</span><HeaderLineBreak />
    <Link to='/beats-for-sale'><CustomButton style={{fontSize: '30px'}}> Listen and Shop for fresh beats</CustomButton></Link>
    <hr/>
        1 Free Stereo Master in Hybrid Chain<br/> 
        <a href={'https://gmail.us7.list-manage.com/subscribe?u=daea54b2881619a66ef815217&id=53f71ba6a2'}><CustomButton style={{fontSize: '30px'}}> Tell Me How!</CustomButton></a>
</BannerText>
  </BannerContainer>
  )
};

export default React.memo(Banner);
