import React from 'react';
import { BannerContainer, BannerText, HeaderLineBreak } from './banner.styles';
import CustomButton from '../custom-button/custom-button.component';
import AudioMeter from './../../components/audio-meter-animation/audio-meter.component';
import MailchimpSubscribeForm from '../mailchimp-form/mailchimp-subscribe-form.component';


const Banner = () => {

  return (


  <BannerContainer>
    <AudioMeter />
      <BannerText>Subscribe for studio rates.<HeaderLineBreak />Plus first notice on new releases.<hr/>Exclusive and Non-exclusive licensing.<HeaderLineBreak />Stems, Mastered Wav & NFTs available.

      <div id="mc_embed_signup">
          <MailchimpSubscribeForm />
        </div>

      <HeaderLineBreak />You keep 100% royalties as an artist.<HeaderLineBreak /><CustomButton> Listen to Fresh Beats for Sale!</CustomButton></BannerText>
  </BannerContainer>
  )
};

export default React.memo(Banner);
