import React from 'react';
import { BannerContainer, BannerText, HeaderLineBreak } from './banner.styles';
import CustomButton from '../custom-button/custom-button.component';
import AudioMeter from './../../components/audio-meter-animation/audio-meter.component';
import MailchimpSubscribeForm from '../mailchimp-form/mailchimp-subscribe-form.component';


const Banner = () => {

  return (


  <BannerContainer>
    <AudioMeter />
      <BannerText>
        <span style={{fontSize: '2.3em', paddingTop: '25px'}}>FRESH BEATS FOR SALE</span><HeaderLineBreak />
          Exclusive and Non-exclusive Licencing
          <HeaderLineBreak />
          <span style={{fontSize: '.5em'}}>You keep 100% royalties as an artist when you publish<HeaderLineBreak />
          Stems, Unmastered & Mastered Wav, & NFTs available</span>
          <CustomButton style={{fontSize: '30px'}}> Shop for fresh beat licenses</CustomButton>
          <hr/>
      Subscribe for 1 free master<HeaderLineBreak />Plus a studio rate sheet<HeaderLineBreak />
      <div id="mc_embed_signup">
        <MailchimpSubscribeForm />
      </div>
    <HeaderLineBreak />
      </BannerText>
  </BannerContainer>
  )
};

export default React.memo(Banner);
