import React, { useEffect} from 'react';

import { BannerContainer, BannerTextBackgroundGreen, BannerTextBackgroundYellow, BannerTextBackgroundRed, BannerText } from './banner.styles';

const Banner = () => {


  useEffect(() => {
    const script = document.createElement("script");
    script.async = true;
    script.src = "https://fierce-pioneer-8487.ck.page/cb3abfc49b/index.js";
    script.onload = () => this.scriptLoaded();



    //For head
    document.head.appendChild(script);

    // For body
    document.body.appendChild(script);

    // For component
    this.div.appendChild(script);


    return () => {

    }
  }, [])


  return (


  <BannerContainer>
    <BannerTextBackgroundRed>
      <BannerText></BannerText>
    </BannerTextBackgroundRed>
    <BannerTextBackgroundYellow>
      <BannerText></BannerText>
    </BannerTextBackgroundYellow>
    <BannerTextBackgroundGreen>
      <BannerText>Subscribe to get a free mix, master or beat. I'll be in touch within 24 hours.</BannerText>
      <script data-uid='cb3abfc49b' />


    </BannerTextBackgroundGreen>
  </BannerContainer>
  )
};

export default React.memo(Banner);
