import React, { useState, useRef } from 'react';
import { connect } from 'react-redux';
import { createStructuredSelector } from 'reselect';

import CartIcon from '../cart-icon/cart-icon.component';
import CartDropdown from '../cart-dropdown/cart-dropdown.component';
import { selectCartHidden } from '../../redux/cart/cart.selectors';
import { selectCurrentUser } from '../../redux/user/user.selectors';
import { signOutStart } from '../../redux/user/user.actions';

import { Burger } from '../burger/burger.component';
import { ResponsiveMenu } from '../responsive-menu/responsive-menu.component';
import { useOnClickOutside } from '../../hooks';



import {
  MainHeaderContainer,
  HeaderContainer,
  LogoContainer,
  LogoMain,
  OptionsContainer,
  OptionLink,
  SpecialText,
  LogoText,
  LogoSubText,
  SignOutAndInLink
  } from './header.styles';

const Header = ({ currentUser, hidden, signOutStart }) => {
  const [open, setOpen] = useState(false);
  const node = useRef();
  useOnClickOutside(node, () => setOpen(false));

  return (
  <MainHeaderContainer>
      <HeaderContainer>
        <LogoContainer to='/'>
          <LogoMain />
          <LogoText>DALE STREET STUDIO</LogoText>
        </LogoContainer>
        <OptionsContainer>
          <SpecialText isActive={false}>Now Booking Clients</SpecialText>
          <OptionLink
            className='material-icons'
            exact
            to='/'
            activeStyle={{
              fontWeight: "bold",
              color: `"#faae2b"`,
              fontSize: "35px"
            }}
          >
            home
          </OptionLink>
          <OptionLink
            exact
            to='/services'
            activeStyle={{
              fontWeight: "bold",
              color: "#faae2b",
              fontSize: "25px"
            }}
          >
            AUDIO SERVICES
          </OptionLink>
          <OptionLink
            exact
            to='/about'
            activeStyle={{
              fontWeight: "bold",
              color: "#faae2b",
              fontSize: "25px"
            }}
          >
            ABOUT
          </OptionLink>
          <OptionLink
            exact
            to='/shop'
            activeStyle={{
              fontWeight: "bold",
              color: "#faae2b",
              fontSize: "25px"
            }}
          >
           BeatStyle Merch
          </OptionLink>
          <a style={{ color: 'white', padding: '15px' }} href={'https://us7.list-manage.com/contact-form?u=daea54b2881619a66ef815217&form_id=5187f677a6d5390aef0c1e09d8470b9e'}>Contact</a>

        </OptionsContainer>

        {currentUser ? (
          <SignOutAndInLink as='div' className='option' onClick={signOutStart}>
            SIGN OUT
          </SignOutAndInLink>
        ) : (
          <SignOutAndInLink
            exact
            to='/sign-in'
            activeStyle={{
              fontWeight: "bold",
              color: "#faae2b"
            }}
          >
            SIGN IN
          </SignOutAndInLink>
        )}

        <CartIcon className='option' />
        {hidden ? null : <CartDropdown />}
        <div ref={node}>
          <Burger open={open} setOpen={setOpen} />
          <ResponsiveMenu open={open} setOpen={setOpen}></ResponsiveMenu>
        </div>

    </HeaderContainer>
  </MainHeaderContainer>
  );
};



const mapStateToProps = createStructuredSelector({
  currentUser: selectCurrentUser,
  hidden: selectCartHidden
});

const mapDispatchToProps = dispatch => ({
  signOutStart: () => dispatch(signOutStart())
});

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(Header);
