import styled from 'styled-components';

export const BannerContainer = styled.div`
  background: linear-gradient(to top, rgba(250,174,43, 0.6) 10%, transparent),
    url("/client/src/images/WebPFiles/Banner-Background.webp"), url("/client/src/images/JP2Files/Banner-Background.jp2"), url("/client/src/images/JXRFiles/Banner-Background.jxr"), url("/client/src/images/") left 10% bottom 20%/cover no-repeat border-box,
    #ff1b68;
  /* background-color: #faae2b; */
