import styled from 'styled-components';

export const SignUpContainer = styled.div`
  display: flex;
  flex-direction: column;
  width: 380px;

  @media screen and (max-width: 800px) {
    margin-left: 0;
    margin-right: 0;
    margin-top: 50px;
    width: 100%;
    }
`;

export const SignUpTitle = styled.h2`
  margin: 10px 0;
`;
