          </CustomButton>
      </section>
      </MusicPlayerButtonList>
      ) : (
        <div style={{ display: 'none'}}></div>
      )}

    {unmount ? null : (
      <ReactJkMusicPlayer
        {...params}
        onThemeChange={(theme) => {
          console.log('onThemeChange: ', theme)
          this.updateParams({ theme })
        }}
        onModeChange={(mode) => {
